    // Fills the mapString arrow code with the mapping timings from the given variable
    public void loadSongMapping(String song) {
        switch (song) {
            case "trapqueen":
                mapString = trapQueen;
                break;
            case "nokia":
                mapString = nokia;
                break;
            case "takeu":
                mapString = takeU;
                break;
            case "luther1":
                mapString = luther1;
                break;
            case "luther2":
                mapString = luther2;
                break;
            case "testnotes":
                mapString = testnotes;
                break;
            case "tweaker":
                mapString = tweaker;
                break;
            case "darlingI":
                mapString = darlingI;
                break;
            case "thespins":
                mapString = thespins;
                break;
        }
    }

    // Runs through the whole mapped string and adds the arrows
    // Code contains the letter A, the timing of the key in hectoseconds, the letter T, and then the direction of the arrow.
    public boolean parseMapString(boolean luther, boolean aSyncTwo) {
        // If there is another key to be mapped
        if (mapString.indexOf("A") == -1) {
            return false;
        }
        mapString = mapString.substring(1);
        double time = Float.parseFloat(mapString.substring(0,mapString.indexOf("T"))) / 100;
        mapString = mapString.substring(mapString.indexOf("T")+1);
        int dir = Integer.parseInt(mapString.substring(0,1));
        mapString = mapString.substring(1);

        if (!luther) {
            backend.addArrow(time, dir, false);
            if (backend.getWindow().getGameState() == "two player song")
                backend.addArrow(time, dir, true);
        } else {
            if (aSyncTwo)
                backend.addArrow(time, dir, true);
            else
                backend.addArrow(time, dir, false);
        }

        return true;
    }

    public void clearMapString() {
        mapString = "";
    }


